INSERT INTO `xbmall_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `is_buy`, `buymax`, `buymax_start_date`, `buymax_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `zhekou`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `supplier_id`, `supplier_status`, `supplier_status_txt`, `is_check`, `is_catindex`, `cost_price`, `valid_date`, `is_virtual`, `exclusive`, `ghost_count`, `area_cat_id`, `is_pass`, `reason` ) VALUES  ('5', '280', 'ECS000005', '百痛康贴', '+', '16', '0', '', '0', '0.000', '144.00', '120.00', '0.00', '0', '0', '0', '0', '0', '0', '1', '', '', '<p style=\"text-align: left;\">关于我们膏药的基本说明和使用方式方法大概时间\</p><p style=\"text-align: left;\">\1）颈椎病\</p><p style=\"text-align: left;\">基本症状：眩晕、头痛、失眠健忘、头昏脑涨、视觉障碍、突然摔麻木、脖子僵硬、坐立不安\</p><p style=\"text-align: left;\">一张见效，两张很舒服，三张很开心，四张五张真幸福\</p><p style=\"text-align: left;\">\2）肩周炎\</p><p style=\"text-align: left;\">基本症状：简称“冻结肩”、“肩凝症”、“漏肩风”，日久者肌肉萎缩、关节僵硬、举手困难\</p><p style=\"text-align: left;\">一张举手自然，两张冷痛不见，三张很舒服，四张很开心，五张真幸福，六张以后知道花小钱也可以买健康\</p><p style=\"text-align: left;\">\3）骨质增生：</p><p style=\"text-align: left;\">基本症状：多见于：颈、腰椎、胸椎、手指、脚跟等处，患者痛苦不堪、难以言表\</p><p style=\"text-align: left;\">一张立减症状，两张真舒服，三张真开心，四张真幸福，五张开始讲儿女孝顺，六张以后介绍病友\</p><p style=\"text-align: left;\">\4）腰椎间盘突出：</p><p style=\"text-align: left;\">基本症状：表现于腰部酸、麻、胀、痛、压迫每条腿跟五条坐骨神经，无论哪根开始放射性疼痛，出现腿疼、麻等很不舒服的感觉\</p><p style=\"text-align: left;\">一张见真情，两张赞神奇，三张真舒服，四张很开心，五张真幸福，六张开始讲过去自己工作太辛苦现在儿女如何关心孝顺的问题，七张以后开始介绍病友\</p><p style=\"text-align: left;\">\5）风湿、类风湿性关节炎\</p><p style=\"text-align: left;\">基本症状：常见于大小关节不舒服直到痛的喊救命，大关节积水，小关节变形，痛苦不堪，无法自理\</p><p style=\"text-align: left;\">一张两张如见救命稻草，三张积水基本全无，四张真舒服，五张好开心，六张真幸福，七张找发明人，八张以后介绍病友\</p><p style=\"text-align: left;\">\6）滑膜炎、半月板损伤、腱鞘炎、网球肘、顽固性跌打损伤等\</p><p style=\"text-align: left;\">基本症状：常见于过度劳累的教师、打字员、厨师、运动员、手机低头族、职业病员等\</p><p style=\"text-align: left;\">一张见神奇，两张立减症状，三张真舒服开心，四张开始脑子记百痛康贴头像，五张遇人便讲该贴使用后的方式方法和心得，六张以后开始介绍亲朋好友，大力推荐\</p><p style=\"text-align: left;\">\7）强直性脊髓炎（包括腰肌劳损）\</p><p style=\"text-align: left;\">基本症状：晨僵：早上起床翻身都难，必须想了五分钟左右才能翻身起床；蹲僵：蹲下去想站起来都难，必须想了五分钟左右才能站起来，目前世界难题，国家也没有好的办法\</p><p style=\"text-align: left;\">一张两张三张不需多想伸展自然见奇迹，四张五张好像恢复了一半，六张七张八张只想感谢发明创始人，九张以后开始介绍同类病友，谈：车到山前必有路，骨病自有百痛康，生活原来如此美好，中医中药在习主席的领导下发扬光大是中国强大繁荣的必然之路\</p><p style=\"text-align: left;\">\8）股骨头坏死：有人贴了十张呱呱叫，具体没有病例我们无法深层次找案例，不好意思\</p><p><br/></p><p style=\"text-align: left;\"><img src=\"http://xiangbai.king51.com/includes/ueditor/php/../../../images/image/79871491036464.jpg\" style=\"\"/></p><p style=\"text-align: left;\"><img src=\"http://xiangbai.king51.com/includes/ueditor/php/../../../images/image/76531491036464.jpg\" style=\"\"/></p><p style=\"text-align: left;\"><img src=\"http://xiangbai.king51.com/includes/ueditor/php/../../../bdimages/upload1/20170401/1491036756276837.jpg\" title=\"20170401149103670058df6a1c820eb.jpg\"/></p>', 'images/201704/thumb_img/5_thumb_G_1491036395164.jpg', 'images/201704/goods_img/5_G_1491036395504.jpg', 'images/201704/source_img/5_G_1491036395722.jpg', '1', '', '1', '1', '0', '10', '1491036395', '100', '0', '0', '0', '0', '0', '10.0', '0', '1492667453', '0', '', '0', '-1', '0', '0', '0', '', '', '0', '0.00', '', '0', '-1', '0', '1', '0', '');
